#include <stdio.h>
int line3(int n);
int tri3(int n);

int main(void) {
    /*入力:1つの整数, 
    出力:1行目に'*'、2行目に'**'…n行目に'*'をn個、最後に 'COUNT=' と合計*/
    // 1. 入力を受け取る
        // 段数を格納する変数（n）を定義する。
        int n;
        // scanfを用いて整数を入力し、nに代入する。
        scanf("%d", &n);
    // 2. tri3を呼ぶ
        // nを引数として tri3 関数を呼び出し、三角形の描画処理を開始する。
        int count = tri3(n);
        printf("\nCOUNT=%d\n", count);
    return 0;
}

// tri3関数を作成する
int tri3(int n){
    // 再帰の終了条件をチェックする（n > 0 のときだけ実行）。
    int count = 0;
    if(n > 0){
        // 【再帰呼び出し】自分より小さい三角形（n-1段）を先に描画させるため、tri3(n-1) を呼ぶ。
        count += tri3(n - 1);
        // 現在の行（n個の星）を描画するため、line3(n) を呼ぶ。
        count += line3(n);
    }
    return count;
}

// line3関数を作成する
int line3(int n){
    // 再帰の条件分岐を行う。
    int count = 0;
    if(n > 0){
        // もし n > 0 なら：
            // 画面に '*' を1つ出力する。
            printf("*");
            count = 1;

            // 残りの星（n-1個）を描画するため、line3(n-1) を再帰的に呼ぶ。
            count += line3(n - 1);
    } else {
        // もし n == 0 なら：
            // 再帰を終了する。
            printf("\n");
    }
    return count;
}
