#include <stdio.h>
int main(void) {
    // Input:one integer, Output:sum of its divisors
    // 1. Receive the input
    int n;
    scanf("%d", &n);
    // 2. Initialize the sum to 0
    int sum = 0;
    // 3. Loop from 1 to the input value
    for(int i = 1; i<=n; i++){
        // 3-1. Check if the number divides evenly
        if(n%i==0){
            // 3-2. If divisible, add it to the sum
            sum = sum + i;
        }
    }
    // 4. Output the total sum
    printf("%d", sum);
    return 0;
}


