#include <stdio.h>

void pascal(int n) {
    int a[n][n];
    int i, j;
    for (i = 0; i < n; i++) {
        for (j = 0; j <= i; j++) {
            if (j == 0 || j == i) {
                // 三角形の左端と右端に1を格納
                printf("%d", a[i][j]);
                a[i][j] = 1;
            } else {
                // 三角形の中央部分には、前の行の左隣と右隣の要素を足した値を格納
                a[i][j] = a[i - 1][j - 1] + a[i - 1][j];
            }
            printf("%3d ", a[i][j]);
        }
        printf("\n");
    }
}

int main() {
    int n;
    scanf("%d", &n);
    pascal(n);
    return 0;
}
