#include <stdio.h>

int main(void) {
    /*入力:二つの文字列, 出力:文字列を数値とみなした和*/
    //2つの文字列を入力として受け取る。
        // 文字列を受け取るための文字配列（s1, s2）を定義する。
        char s1[100], s2[100];
        // 計算に使用するint型配列（n1, n2, ans）を定義する。
        int n1[100] = {0};
        int n2[100] = {0};
        int ans[100] = {0};
        // 繰り上がりを管理する変数（carry）を0で初期化する。
        int carry = 0;

        // コード記述時に必要な変数の宣言
        int i;
        int len1 = 0;
        int len2 = 0;
        int max_len;
        int print_started = 0;

        // scanfを用いて、空白区切りで2つの文字列を受け取る。
        scanf("%s %s", s1, s2);

    //文字列を、計算しやすいように「位」を揃えて数値配列に格納する。
        // それぞれの文字列の長さを取得する。
        while (s1[len1] != '\0') {
            len1++;
        }
        while (s2[len2] != '\0') {
            len2++;
        }
        max_len = (len1 > len2) ? len1 : len2;

        // 文字列の「末尾（1の位）」が、配列の「0番目」に来るように逆順に変換して格納する。
            // ループ：文字列s1を末尾から先頭に向かってスキャンする。
            for (i = 0; i < len1; i++) {
                // 文字（例:'1'）から '0' を引いて数値（1）にし、配列n1の0番目から順に代入する。
                n1[i] = s1[len1 - 1 - i];
            }
            // ループ：文字列s2も同様に処理し、配列n2に代入する。
            for (i = 0; i < len2; i++) {
                n2[i] = s2[len2 - 1 - i];
            }
    //配列の要素ごとに足し算を行い、繰り上がりを処理する。
        // ループ：桁カウンタ（i）を0から最大桁数まで繰り返す。
        for (i = 0; i <= max_len; i++) {
            // 現在の桁の和を計算する（ n1[i] + n2[i] + 前の桁からのcarry ）。
            int sum = n1[i] + n2[i] + carry;
            // 計算結果の「1の位」（和 % 10）を、答えの配列 ans[i] に保存する。
            ans[i] = sum % 10;
            // 計算結果の「10の位」（和 / 10）を、次の桁への carry として更新する。
            carry = sum / 10;
        }
    //計算結果の配列を、人間が読める形式（左から右）で出力する。
        // ループ：配列の「上の位（最大桁）」から「0番目（1の位）」に向かって逆順に繰り返す。
        for (i = max_len; i >= 0; i--) {
            // 先頭の余計な0を表示しないための制御を行う。
                // 「値が0ではない」または「すでに出力が始まっている」場合のみ数字を表示する。
                if (ans[i] != 0 || print_started) {
                    print_started = 1;
                    // printfで ans[i] を出力する。
                    printf("%d", ans[i]);
                }
        }
        // ループ終了後、改行文字を出力する。
        printf("\n");
    return 0;
}
