#include <stdio.h>

int checktri(int a, int b, int c){
    //三角形の種類判定
    if(a < b + c){
        if(a * a == b * b + c * c){
            return 1;
        }else if(a * a < b * b + c * c){
            return 2;
        }else if(a * a > b * b + c * c){
            return 3;
        }else{
            return 0;
        }
    }
}

