#include <stdio.h>

int checktri(int a, int b, int c){
    //三角形の種類判定
    if(a < b + c){
        if(a * a == b * b + c * c){
            return 1;
        }else if(a * a < b * b + c * c){
            return 2;
        }else if(a * a > b * b + c * c){
            return 3;
        }else{
            return 0;
        }
    }
}

int main(void) {
    /*入力:3つの実数, 出力:三角形の種類*/
    //入力の受付
    int a,b,c;
    scanf("%d", &a);
    scanf("%d", &b);
    scanf("%d", &c);
    //三角形の種類判定と出力
    switch(checktri(a,b,c)){
            case 1;
            printf("直角三角形");
            break:
            case 2;
            printf("鋭角三角形");
            break:
            case 3;
            printf("鈍角三角形");
            break:
            case 0;
            printf("Error");
            break:
    }
    return 0;
}
