#include <stdio.h>
//再帰関数reverse4をつくる
void reverse4(int n){
    if(n > 0){
        printf("%d", n % 10);
        reverse4(n / 10);
    }
}

int main(void) {
    /*入力:一つの整数値, 出力:その値の上位・下位を入れ替えた値*/
    //入力
    int n;
    scanf("%d", &n);
    //再帰関数
    reverse4(n);
    printf("\n");
    return 0;
}
