#include <stdio.h>
//再帰関数reverse4をつくる
void  reverse4(int n, int re, int i){
    if(n > 0){
        re[i] = n % 10;
        //printf("%d", n % 10);
        reverse4(n / 10, re, i++);
        for(int j = 0; re[j] != 0; j++){
        }
        for(j; j < i; j++){
            printf("%d", re[j]);
    }
}

int main(void) {
    /*入力:一つの整数値, 出力:その値の上位・下位を入れ替えた値*/
    //入力
    int n;
    scanf("%d", &n);
    int re[5];
    reverse4(n, re, 0);
    //先頭の０を消したい
    printf("\n");
    return 0;
}
