#include <stdio.h>

int main(void) {
    //aを最大値として辺の長さを作成
    int a,b,c;

    //入力
    scanf("%d",&a);
    scanf("%d",&b);
    scanf("%d",&c);

    //三角形が成立するかを検証する
    if (a < b + c){
        //直角三角形
        if (a^2 == b^2 + c^2) {
            printf("直角三角形\n");
        }
        //鋭角三角形
        else if (a^2 < b^2 + c^2) {
            printf("鋭角三角形\n");
        }
        //鈍角三角形
        else if (a^2 > b^2 + c^2) {
            printf("鈍角三角形\n");
        }
    //成立しない場合
    } else {
        //error
        printf("Error\n");
    }
    return 0;
}
