#include <stdio.h>

int main(void) {
    //aを最大値として辺の長さを作成
    float a,b,c;

    //入力
    scanf("%f",&a);
    scanf("%f",&b);
    scanf("%f",&c);

    //三角形が成立するかを検証する
    if (a < b + c){
        //直角三角形
        //浮動小数点の比較（==）は誤差で期待どおりにならない点は考慮しなくてよい
        if (a*a == b*b + c*c) {
            printf("直角三角形\n");
        }
        //鋭角三角形
        else if (a*a < b*b + c*c) {
            printf("鋭角三角形\n");
        }
        //鈍角三角形
        else if (a*a > b*b + c*c) {
            printf("鈍角三角形\n");
        }
    //成立しない場合
    } else {
        //error
        printf("Error\n");
    }
    return 0;
}
