#include <stdio.h>

void checktri(int a,int b,int c){
    //三角形が成立するかを検証する
    if (a < b + c){
        //直角三角形
        if (a*a == b*b + c*c) {
            return 1;
        }
        //鋭角三角形
        else if (a*a < b*b + c*c) {
            return 2;
        }
        //鈍角三角形
        else if (a*a > b*b + c*c) {
            return 3;
        }
    //成立しない場合
    } else {
        //error
        return 0;
    }
}

int main(void) {
    //aを最大値として辺の長さを作成
    float a,b,c;
    int r; //戻り値代入用

    //入力
    scanf("%f",&a);
    scanf("%f",&b);
    scanf("%f",&c);

    r = checktri(a,b,c);
    switch(r){
        case 1:
            printf("直角三角形\n");
            break:
        case 2:
            printf("鋭角三角形\n");
            break:
        case 3:
            printf("鈍角三角形\n");
            break:
        default:
            printf("Error\n");
            break:
    }

    return 0;
}
