#include <stdio.h>

int yakusum1(int n,int m) {
    int s = 0;
    if (m < n) {
        if (n % m == 0) {
            s = s + m;
        }
        s = s + yakusum1(n,m+1);
    }
        return s;
}

int main(void) {
    /*入力: 1以上1000以下の整数 n, 出力: nの約数の総和は%d\n*/
    int n;
    scanf("%d",&n);
    printf("%dの約数の総和は%d\n",n,yakusum1(n,1));
    return 0;
}
