#include <stdio.h>

int pascal(int n){
    int i,j;
    int pas[10][11];
    for (i==0;i<n;i++) { //縦
        for (j==0;j<=i+1;j++) { //横
            //列数j=0,または列数j=i+1のとき
            if (j==0||j==i+1) {
                pas[i][j]=1; //配列に数字を格納
                printf("%3d",pas[i][j]); //格納した値を表示
                if (j==i+1) {
                    printf("\n"); //改行
                }
            }
            //その他のとき
            else {
                pas[i][j]=pas[i-1][j-1]+pas[i-1][j]; //配列に数字を格納
                printf("%3d\n",pas[i][j]); //格納した値を表示
            }
        }
    }
}

int main(void) {
    /*入力:整数(三角形の高さ), 出力:パスカルの三角形(関数pascalを呼び出して表示)*/
    int n;
    scanf("%d",&n);
    pascal(n);
    return 0;
}
