#include <stdio.h>

int yakusum1(int n);

int main(void) {
    /*入力:一つの整数, 出力:入力した整数の約数の総和*/
    //整数値の入力
    int a,b;
    scanf("%d",&a);
    //再帰関数の内容↓
        //1から入力値まで繰り返して割り切れるかどうかの確認
        //割り切れたら約数である
        //約数SUM（現状の約数の総和）に約数を足す
        //約数SUMを返す
    b=yakusum1(a);
    //出力
    printf("%dの約数の総和は%d\n",a,b);

    return 0;
}

int yakusum1(int n){
    int sum=0;
    for(int i=1;i<=n;i++){
        if(n%i==0)sum=sum+i;
    }

    return sum;
}
