#include <stdio.h>
int tri3(int n,int m); //tri関数
int line3(int n,int m); //line関数

int main(void) {
    /*入力:1つの整数値を入力, 出力:二等辺三角形をアスタリスクで作るのとcountを出力*/
    //入力をする
    int a;
    scanf("%d",&a);
    //整数n,m,countを定義する
    int n;
    int m;
    int count=0;
    //tri関数を再帰する
    tri3(1,a);
    //countの結果を出力する
    printf("COUNT=%d\n",count);
    return 0;
}

int tri3(int n,int m){
    int s;
    if(n<=m){
        line3(1,n);
        tri3(n+1,m);
    }
    return s;
}

int line3(int n,int m){
    int s;
    if(n<=m){
        printf("*");
        s++;
        line3(n+1,m);
    }
    printf("\n");
}
