#include <stdio.h>

int main(void) {
    /*入力:cher型の配列２つ, 出力:int型の配列１つ*/

    //char型の配列を２つ作成、入力, int型に代入
    //int型の出力結果を格納する配列を作成
    char a[20], b[20];
    int c[20] = {}, d[20] = {}, e[20] = {};
    int i, j;
    scanf("%s", a);
    scanf("%s", b);
    for(i = 0; a[i] != '\0'; i++);
    i++;
    for(j = 0; b[j] != '\0'; j++);
    j++;

    int k = 0;
    while(i >= 0){
        c[k] = a[i] - '0';
        i--;
        k++;
    }
    k = 0;
    while(j >= 0){
        d[k] = b[j] - '0';
        j--;
        k++;
    }

    //ループを用いて各桁の文字をを足す
        //もし繰り上がりが発生するが場合は、一つ上の位に1を足す
    for(k = 0; c[k] != 0 || d[k] != 0; k++){
        e[k] += c[k] + d[k];
        if(e[k] >= 10){
            e[k + 1] += 1;
            e[k] -= 10;
        }
    }

    //結果を出力
    if(e[k] == 0)
        k--;
    while(k >= 0){
        printf("%d", e[k]);
        k--;
    }

    return 0;
}
