#include <stdio.h>

int main(void) {
    /*入力:二つの文字列, 出力:文字列を10進数とみなしたときの和*/
    char a[11],b[11];
    int A=0,B=0;
    //文字列を入力する
    scanf("%s %s",a,b);
    //for文を使い文字列の長さを取得する
    for(int i=0;a[i]!='\0';i++){
        A=A+1;
    }
    for(int j=0;b[j]!='\0';j++){
        B=B+1;
    }
    //文字列の中で位の小さいものからint型の整数に変換してint型の配列に格納する
    int x[11],y[11];
    for(int i=0;A>1;i++){
        x[i]=a[A-1];
    }
    for(int j=0;B>1;j++){
        y[j]=b[B-1];
    }
    //繰り返し構文を用いて繰り上げを考慮した足し算を行う
    //計算結果が格納された配列を出力する
    return 0;
}
