#include <stdio.h>

int main(void) {
    float a,b,c;/*入力:3つの実数, 出力:三角形の形状判定*/
    scanf("%f %f %f",&a,&b,&c);
    if(a<b+c){//三角形の成立を判定
        if(a*a==b*b+c*c){//成立なら三角形の種類を判定
            printf("直角三角形\n");
        }
        else if(a*a>b*b+c*c){
            printf("鈍角三角形\n");
        }
        else{
            printf("鋭角三角形\n");
        }
     else{//不成立ならエラーを出力
         printf("Error\n");
    }
        return 0;
}
