#include <stdio.h>

int checktri(float a,float b,float c);

int main(void) {
    /*入力:3つの実数値, 出力:三角形の形状*/
    //三角形の形状を判定する関数checktriを作成
    　//三角形が成立しない：Error
    　//三角形が成立する：三平方の定理を用いて判定
    　　//鈍角三角形、直角三角形、鋭角三角形に分類
    //chectri関数の戻り値をmain関数内のswitch_case文で指定
    　//直角三角形：1
    　//鋭角三角形：2
    　//鈍角三角形：3
    　//Error：0
    float a,b,c;
    int n;
    scanf("%d %d %d",&a,&b,&c);
    n=checktri(a,b,c);



    return 0;
}

int chectri(float a,float b,float c){
    int n;
    if(a+b>c || b+c>a || c+a>b){
        if(a^2+b^2==c^2){
            n=1;
        }else if(a^2+b^2<c^2){
            n=2;
        }else if(a^2+b^2>c^2){
            n=3;
        }
    }else{
        n=0;
    }

    return n;
}
    }
