#include <stdio.h>

int checktri(float a,float b,float c);

int main(void) {
    /*入力:3つの実数値, 出力:三角形の形状*/
    //三角形の形状を判定する関数checktriを作成
    　//三角形が成立しない：Error
    　//三角形が成立する：三平方の定理を用いて判定
    　　//鈍角三角形、直角三角形、鋭角三角形に分類
    //chectri関数の戻り値をmain関数内のswitch_case文で指定
    　//直角三角形：1
    　//鋭角三角形：2
    　//鈍角三角形：3
    　//Error：0
    float a;
    float b;
    float c;
    int n;
    scanf("%f %f %f",&a,&b,&c);
    n=checktri(a,b,c);
    switch(n){
        case 0:
            printf("Error\n");
            break;
        case 1:
            printf("直角三角形\n");
            break;
        case 2:
            printf("鋭角三角形\n");
            break;
        case 3:
            printf("鈍角三角形\n");
            break;
    }
    return 0;
}

int checktri(float a,float b,float c){
    int n;
    if(b + c > a){
        if(c*c + b*b == a*a){
            n=1;
        }else if(c*c + b*b > a*a){
            n=2;
        }else if(c*c + b*b < a*a){
            n=3;
        }
    }else{
        n=0;
    }

    return n;
}
