#include <stdio.h>

int main(void) {
    /*入力:3つの整数値, 出力:三角形の種類*/
    //3つの整数値を入力する
    float a,b,c;
    scanf("%f%f%f",&a,&b,&c);
    //成立するか判定
    if(a<b+c){
        //成立・・・三角形の種類を判定
        if(a*a==b*b+c*c)
            printf("直角三角形");
        else if(a*a<=b*b+c*c)
            printf("鋭角三角形");
        else if(a*a>=b*b+c*c)
            printf("鈍角三角形");
    }else{
        //不成立・・・Error
        printf("Error");
    }
    //結果の表示
    return 0;
}
