#include <stdio.h>
int yakusum1(int n,int i);

int main(void) {
    /*入力:1つの整数値, 出力:約数の総和(整数)*/
    int n;
    //1つの整数値を入力
    scanf("%d",&n);
    //yakusum1に入力値を入れて、それを表示
    printf("%dの約数の総和は%d\n",n,yakusum1(n,1));
    return 0;
}

int yakusum1(int n,int i){
    //返す変数sumをつくる
    int sum=0;
    //判定する数が約数か判定する
    if( n%i == 0 ){
        //約数・・・その数をsumに足す
        sum = sum+i;
    }
    //判定する数が入力値と同値か判定する
    if( n!=i ){
        //同値でない・・・判定する数+1してyakusum1に代入する
        sum = sum+yakusum1(n,i+1);
    }else{
        //同値・・・sumを返す
        return sum;
    }
    return sum;
}
