#include <stdio.h>
int line3(int i);
int tri3(int n,int m);

int main(void) {
    /*入力:1つの整数値, 出力:*の三角形*/
    //整数値の入力
    int n;
    int count;
    scanf("%d",&n);
    //tri3入力値を代入
    count=tri3(n,1);
    //countを表示
    printf("COUNT=%d",count);
    return 0;
}

int line3(int i){
    int count=0;
    //i個の*を表示
    //*を一つずつ表示、iが0になるまで再帰
    //iが0かどうか判定

    if(i!=0){
        //i!=0・・・*を表示
        printf("*");
        //count = count+1
        count = count+1;
        //i-1をline3に代入
        line3(i-1);
        }else{
        //i==0・・・countを返す
        return count;
    }
    return count;
}

int tri3(int n,int m){
    int count=0;
    //line3にmを代入
    count=line3(m);
    printf("\n");
    //nがmと同値か判断

    if(n==m){
        //同値・・・countを返す
        return count;
    }else{
        //同値でない・・・tri3にn,m+1を代入
        count= count+tri3(n,m+1);
    }
    return count;
}
