#include <stdio.h>

int main(void) {
    /*入力:三つの実数, 出力:三角形の形状判定またはError*/
    //入力：三つ値
    int a,b,c;
    scanf("%d %d %d",&a,&b,&c);
    //三角形の成立条件から考える
    if(a>=b+c){
        printf("Error\n");
    }
        //三角形が成立しない場合：Ｅｒｒｏｒ
        //三角形が成立する場合：形状判定
    if(a*a==(b*b)+(c*c)){
        printf("直角三角形\n");
    }else if(a*a<(b*b)+(c*c)){
        printf("鋭角三角形\n");
    }else{
        printf("鈍角三角形\n");
    }
            //三角形の種類を求める
    //出力：三角形の形状

    return 0;
}
