#include <stdio.h>

int main(void) {
    /*入力:3つの実数値, 出力:三角形の種類またはError*/
    //入力を受け取る
    //3角形が成立するか確かめる
        //入力1の長さが他の2辺の長さの和よりおおきい
    //3角形の種類を確認する
    int a,b,c;
    scanf("%d %d %d",&a,&b,&c);
    if(a>b+c){
        printf("Error\n");
    }else{
        if(a*a==b*b+c*c){
            printf("直角三角形\n");
        }else if(a*a<*b+c*c){
            printf("鋭角三角形\n");
        }else{
            printf("鈍角三角形\n");
        }
    }
    return 0;

    return 0;
}
