#include <stdio.h>

int main(void) {
    /*入力:1つの整数, 出力:約数の総和*/
    //入力値を受け取る
    //1から入力値の値になるまで1ずつ増やしながら入力値をわる
    //あまりが0になる数を足す
    int x,y;
    int yakusum1(int a,int b){
        for(int i=0;i<a;i++){
            if(a%b==0){
                y=y+yakusum1(a-1,1);
            }
        }
        return y;
    }
      scanf("%d",&x);
      printf("%d",yakusum1(x,1));
    return 0;
}
