#include <stdio.h>
int yakusum1(int a,int b,int c){
        do{
            if(a%b==0){
                c=c+yakusum1(a-1,1);
            }
            b=b+1;
        }while(a<b);
    return c;
}
int main(void) {
    /*入力:1つの整数, 出力:約数の総和*/
    //入力値を受け取る
    //1から入力値の値になるまで1ずつ増やしながら入力値をわる
    //あまりが0になる数を足す
        int x;
      scanf("%d",&x);
      printf("%d",yakusum1(x,1,0));
    return 0;
}
