#include <stdio.h>
int yakusum1(int a);

int main(void) {
    /*入力:1つの整数x, 出力:入力値の約数の総和*/
    /*入力値の約数を求める　たとえば10の約数は、1,2,5,10*/
    /*繰り返し文で、iが1~xまでの間、10%i==0のとき、10/iをカウント*/

    int x;
    scanf("%d",&x);

    int count = yakusum1(int x);
    printf("%dの約数の総和は%d",x,count);

    return 0;
}

int yakusum1(int a){
    int count = 0;
    for(int i=1;i<=a;i++){
        if(10%i == 0){
            int wari = 10/i;
            count = count + wari;
        }
    }
    return count;
}
