#include <stdio.h>
int yakusum1(int a);

int main(void) {
    /*入力:1つの整数x, 出力:入力値の約数の総和*/
    /*入力値の約数を求める　たとえば10の約数は、1,2,5,10*/
    /*繰り返し文で、iが1~xまでの間、x%i==0のとき、x/iをカウント*/

    int x;
    scanf("%d",&x);

    int count = yakusum1(x);
    printf("%dの約数の総和は%d",x,count);

    return 0;
}

int yakusum1(int a){
    int count = 0;
    for(int i=1;i<=a;i++){
        if(a%i == 0){
            int wari = a/i;
            count = count + wari;
        }
    }
    return count;
}



int yakusum1(int a){
    static int n = 0;     // 元の入力値を保持
    static int i = 1;     // 1～n を回すためのカウンタ
    static int sum = 0;   // 約数の総和

    // 最初の呼び出しだけ初期化
    if (n == 0) {
        if (a <= 0) return 0;  // 必要ならエラー処理に変えてOK
        n = a;
        i = 1;
        sum = 0;
    }

    // 終了条件
    if (i > n) {
        int ans = sum;
        n = 0;   // 次回呼び出しに備えてリセット
        return ans;
    }

    // 元のforの中身
    if (n % i == 0) {
        sum += n / i;
    }

    i++;
    return yakusum1(a); // 次へ（再帰）
}
※注意：static を使ってるので、同時に複数回計算したり（別スレッド等）には向かないけど、課題レベルならこれでOKです。










