#include <stdio.h>
int yakusum1(int a,int i);

int main(void) {
    /*入力:1つの整数x, 出力:入力値の約数の総和*/
    /*入力値の約数を求める　たとえば10の約数は、1,2,5,10*/
    /*繰り返し文で、iが1~xまでの間、x%i==0のとき、x/iをカウント*/
    /*x < */
    /*x%i==0のとき、x/iを再帰関数で足し合わせる*/

    int x;
    scanf("%d",&x);

    int count = yakusum1(x,1);
    printf("%dの約数の総和は%d",x,count);

    return 0;
