#include <stdio.h>

int main(void) {
    int a,b,c;
    /*入力:3つの実数値, 出力:三角形の種類かError*/
    scanf("%d %d %d",&a,&b,&c);
    /*三角形が成立するかを判定する*/
    if(a>b+c){
        printf("Error");
      /*三角形が直角三角形か鈍角三角形か鋭角三角形かを判定する*/
    }else{
        if(a*a<b*b+c*c){
            printf("鋭角三角形");
        }else if(a*a=b*b+c*c)
            printf("直角三角形");
        }else{
            printf("鈍角三角形");
        }  
    }
    return 0;
}
