#include <stdio.h>

int main(void) {
    /*入力:３つ実数, 出力:三角形の形状*/

    int a,b,c;//３つの実数入力

    if(a>b+c){//三角形が成立する
        //三角形の形状判定
        if(a*a==b*b+c*c){
            printf("直角三角形");//直角三角形
        }else if(a*a<b*b+c*c){
            printf("鋭角三角形");//鋭角三角形
        }else if(a*a>b*b+c*c){
            printf("鈍角三角形");//鈍角三角形
        }

    }else{//三角形が成立しない
        printf("error");//error
        }

    return 0;
}
