int checktri(float a,float b,float c){
    int n;
    if(a+b>c || b+c>a || c+a>b){
        if(a*a+b*b==c*c){
            n=1;
        }else if(a*a+b*b<c*c){
            n=2;
        }else if(a*a+b*b>c*c){
            n=3;
        }
    }else{
        n=0;
    }

    return n;
}
    }

