    //入力をパスカルの三角形の段数として受け付ける。
        //段数を代入するための変数を準備する。
    int n;
        //準備した変数に入力値を代入する。
    scanf("%d",&n);
    //入力値（段数）をpascal関数に渡す。
        //入力された値を引数としてpascal関数を呼び出す。
    pascal(n);
    return 0;
}
//入力された段数分のパスカルの三角形を出力するpascal関数を作る
void pascal(int n){
    //計算結果を記録する表（二次元配列）や配列のカウントをする変数を準備する。
        //計算結果を記録するための表（二次元配列）を準備する。
    int triangle[n+1][n+1];
        //表の行と列を指し示すカウント変数（iとj）を準備する。
    int i,j;
    //表の値を計算し、埋める。
        //ループ：行カウンタ（i=0）の値を指定された段数まで進める。
    for(i = 0; i < n; i++){
            //ループ：列カウンタ（j=0）の値を現在の行番号iまで進める。
        for(j = 0; j <= i; j++){
                //現在のマス（i行j列）が行の端か調べる。
                    //条件分岐：行の端である場合。
            if(j == 0 || j == i){
                        //現在のマスに「1」を代入する。
                triangle[i][j] = 1;
                    //条件分岐：行の端でない場合。
            } else {
                        //現在のマスに左斜め上の値（i-1行j-1列）と真上の値（i-1行j列）の合計を記録する。
                triangle[i][j] = triangle[i-1][j-1] + triangle[i-1][j];
            }
        }
    }
    //表を三角形になるように出力する。
        //ループ：出力を実現するために、行カウンタ（i=1）の値を指定された段数まで進める。
    for(i = 0; i < 10; i++){
            //ループ：列カウンタ（j=0）の値を現在の行番号iまで進める。
                //現在のマスの値をとりだし、指定された書式%3dで出力する。
        printf("%3d",triangle[i][j]);
        //行の終わりに改行をする。
        printf("\n");
    }
//pascal関数を終了する。
}

